import React from "react";
import styled from "styled-components";


function WriteComponent() {

    const imageInput = React.createRef();

    const onButtonClick = (e) => {
      imageInput.current.click();
    };

    return(
        <>
            <ImageInput type="file" accept="image/jpg, image/jpeg, image/png" ref={imageInput} style={{display: "none"}}></ImageInput>
            <AddImageButton type="button" onClick={onButtonClick}>이미지 넣기</AddImageButton>
        </>
    )

}

export default WriteComponent;

const ImageInput = styled.input`

`

const AddImageButton = styled.button`
    width: 100px;
    border-radius: 0.3rem;
    border: 1px solid ;
    background-color: rgb(254,213,107);

    margin-left: 1rem;

    font-size: 15px;
    color: rgb(234,130,99);
    text-align: center;

    cursor: pointer;
    user-select: none;
    transition: .2s all;
`
