import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";

export default function RegisterComponent() {
    const [name, setName] = useState("");
    const [tel, setTel] = useState("");
    const [id, setId] = useState("");
    const [password, setPassword] = useState("");

    const onNameHandler = (e) => {
        setName(e.currentTarget.value);
    }
    const onTelHandler = (e) => {
        setTel(e.currentTarget.value);
    }
    const onIdHandler = (e) => {
        setId(e.currentTarget.value);
    }
    const onPwHandler = (e) => {
        setPassword(e.currentTarget.value);
    }
    const onSubmitHandler = (e) => {
        e.preventDefault(); //submit 했을 떄 리프레쉬 방지
        console.log(name, tel, id, password);
    }
    return(
        <Positioner>
            <Logo>Register</Logo>
            <Contents>
                <form onSubmit={onSubmitHandler}>
                    <Label htmlFor="name">이름</Label>
                    <br />
                    <Input type="text" id="name" onChange={onNameHandler} placeholder="홍길동"></Input>
                    <Label htmlFor="tel">전화번호 (010-0000-0000)</Label>
                    <br />
                    <Input type="text" id="tel" onChange={onTelHandler} placeholder="010-1234-5678"></Input>
                    <Label htmlFor="id">아이디</Label>
                    <br />
                    <Input type="text" id="id" onChange={onIdHandler} placeholder="abc123"></Input>
                    <br />
                    <Label htmlFor="id">비밀번호</Label>
                    <br />
                    <Input type="password" id="password" onChange={onPwHandler} placeholder="def1234"></Input>
                </form>            
            </Contents>
            <Button type="submit" onClick={onSubmitHandler}>회원가입</Button>
            <RegisterLink><NavLink to="/login">로그인 페이지로</NavLink></RegisterLink>

        </Positioner>
    )

}

const Positioner = styled.div`
    margin: auto;
    margin-top: 8rem;
    width: 500px;
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.8);
    position: relative;
    padding-bottom: 0.01rem;
    border-radius: 0.5rem;
`;

const Logo = styled.div`
    background-color: #E9AC06;
    height: 5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    border-top-left-radius: 0.5rem;
    border-top-right-radius: 0.5rem;

    color: white;
    font-family: 'Rajdhani';
    font-size: 2.4rem;
    letter-spacing: 5px;
    text-decoration: none;
`;

const Contents = styled.div`
    background: white;
    padding: 2rem;
    height: auto;

`;

const Input = styled.input`
    width: 95%;
    border: 1px solid grey;
    border-radius: 0.5rem;
    outline: none;
    line-height: 2.5rem;
    padding-left: 0.5rem;
    padding-right: 0rem;
    margin-right: 1rem;

    font-size: 1.2rem;
    color: grey;
`;

const Label = styled.label`
    font-size: 0.8rem;
    color: grey;
`

const Button = styled.button`
    width: 92%;
    margin-top: 1rem;
    margin-left: 1rem;
    margin-right: 1rem;
    padding-top: 0.5rem;
    padding-bottom: 0.5rem;

    background: #E9AC06;
    color: white;

    text-align: center;
    font-size: 1rem;
    font-weight: 500;

    cursor: pointer;
    user-select: none;
    transition: .2s all;
`

const RegisterLink = styled.div`
    margin-top: 0.8rem;
    margin-right: 2rem;
    margin-bottom: 1rem;

    text-align: right;
    font-size: 0.8rem;
    color: grey;
`
