import React, { useState } from "react";
import styled from "styled-components";

export default function LoginComponent() {
    const [id, setId] = useState("");
    const [password, setPassword] = useState("");

    const onIdHandler = (e) => {
        setId(e.currentTarget.value)
    }
    const onPwHandler = (e) => {
        setPassword(e.currentTarget.value)
    }
    const onSubmitHandler = (e) => {
        e.preventDefault(); //submit 했을 떄 리프레쉬 방지
        console.log(id, password)
    }
    return(
        <Positioner>
            <LogoWrapper>Login</LogoWrapper>
            <form onSubmit={onSubmitHandler}>
                <label htmlFor="id">ID : </label>
                <input type="text" id="id" onChange={onIdHandler} style={{textAlign: 'center'}}></input>
                <br />
                <label htmlFor="id">PW : </label>
                <input type="text" id="password" onChange={onPwHandler} style={{textAlign: 'center'}}></input>
                <br />
                <button type="submit">로그인</button>
            </form>
        </Positioner>
    )

}

const Positioner = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    boxShadow: 0 5px 20px rgba(0, 0, 0, 0.8);
`;

const LogoWrapper = styled.div`
    background-color: #E9AC06;
    height: 5rem;
    display: flex;
    align-items: center;
    justify-content: center;

    color: white;
    font-family: 'Rajdhani';
    font-size: 2.4rem;
    letter-spacing: 5px;
    text-decoration: none;
`;
