import React, { useEffect, useState } from "react";
import styled from "styled-components";

function SearchBar() {
    const [searchText, setSearchText] = useState()
    const bhc_api = ["가나다", "용인서천", "영통"]
    const onSearchHandler = (e) => {
        setSearchText(e.currentTarget.value);
    }
    const onSubmitHandler = (e) => {
        console.log(searchText);
    }
    useEffect(() => {
        console.log(searchText)
    }, [searchText])
    return(
        <div style={{marginTop: '3rem'}}>
            <div style={{display: "flex", justifyContent: 'center'}}>
                <Input onChange={onSearchHandler} placeholder="주소를 검색하세요"></Input>
                <Button onClick={onSubmitHandler}>검색</Button>
            </div>

            <AutoSearchContainer>
                <AutoSearchWrap>
                    {bhc_api.map((search, idx) => (
                        <AutoSearchData
                            key={search.name}
                            onClick={() => {
                                setSearchText(search.name);
                            }}
                        ></AutoSearchData>
                    ))}
                </AutoSearchWrap>
            </AutoSearchContainer>

        </div>
    )

}

export default SearchBar;

const Input = styled.input`
    width: 500px;
    height: 30px;
    border-radius: 0.5rem;
    border: 1px solid rgb(151,142,113);

`

const Button = styled.button`
    width: 50px;
    border-radius: 1rem;
    border: 1px solid rgb(151,142,113);
    background-color: rgb(254,213,107);

    margin-left: 1rem;

    font-size: 15px;
    color: rgb(234,130,99);
    text-align: center;

    cursor: pointer;
    user-select: none;
    transition: .2s all;
`

const AutoSearchContainer = styled.div`
    width: 500px;
    height: 50vh;
    border: 1px solid rgb(151,142,113);
`

const AutoSearchWrap = styled.ul`

`;

const AutoSearchData = styled.li`
  padding: 10px 8px;
  width: 100%;
  /* height: 30px; */
  font-size: 14px;
  font-weight: bold;
  z-index: 4;
  letter-spacing: 2px;
  &:hover {
    background-color: #edf5f5;
    cursor: pointer;
  }
  position: relative;
  img {
    position: absolute;
    right: 5px;
    width: 18px;
    top: 50%;
    transform: translateY(-50%);
  }
`;
