import React from "react";
import { NavLink } from "react-router-dom";
import NavBar from "../components/Common/NavBar";
import {motion} from 'framer-motion';
import styled from "styled-components";
import {GLTFLoader} from 'three/examples/jsm/loaders/GLTFLoader';
import * as THREE from 'three';
import './style.css';

export default function HomeBeforeLogin() {
    const scene = new THREE.Scene()

    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000)

    const renderer = new THREE.WebGL1Renderer({canvas: document.querySelector('#app')})

    renderer.setPixelRatio(window.devicePixelRatio)

    renderer.setSize(window.innerWidth, window.innerHeight)

    camera.position.setZ(30)

    renderer.render(scene, camera)

    const geometry = new THREE.TorusGeometry(10, 3, 16, 100)

    //const material = new THREE.MeshBasicMaterial({color: 0xFF6347, wireframe: true})

    const material = new THREE.MeshStandardMaterial({color: 0xFF6347})

    const torus = new THREE.Mesh(geometry, material)

    scene.add(torus)

    const pointLight = new THREE.PointLight(0xffffff)

    pointLight.position.set(20, 20, 20)

    const ambientLight = new THREE.AmbientLight(0xffffff)

    scene.add(pointLight, ambientLight)

    const lightHelper = new THREE.PointLightHelper(pointLight)

    const gridHelper = new THREE.GridHelper(200, 50)

    scene.add(lightHelper, gridHelper)

    //const controls = new OrbitControls(camera, renderer.domElement)

    function addStar(){

      const geometry = new THREE.SphereGeometry(0.25, 24, 24)

      const material = new THREE.MeshStandardMaterial({color: 0xffffff})

      const star = new THREE.Mesh(geometry, material)

      const [x, y, z] = Array(3).fill().map(() => THREE.MathUtils.randFloatSpread(100))

      star.position.set(x, y, z)

    }

    Array(200).fill().forEach(addStar)

    const spaceTexture = new THREE.TextureLoader().load('./images/spaceImage.png')

    scene.background = spaceTexture

    function animate(){

      requestAnimationFrame(animate)

      torus.rotation.x += 0.01

      torus.rotation.y += 0.005

      torus.rotation.z += 0.01

      renderer.render(scene, camera)

      //controls.update()

    }

    animate()
    return(
        <>
            <div>
                <NavBar>
                    <div><NavLink to='/'></NavLink></div>
                </NavBar>
            </div>
            <div style={{position: 'relative', height: '650px'}}>
                <img alt="chicken" src="img/배경.png" style={{width: '100%', height: '650px', marginBottom: '0px'}} ></img>
                <div style={{position: 'absolute', top: "50px"}}>
                <motion.div
                animate={{ scale: [1, 1.3, 1] }}
                transition={{ duration: 2, times: [0, 0.2, 0.5] }}
                ><Img alt="chicken" src="img/그림1.png"></Img></motion.div></div>
                <canvas id="canvas" width="300" height="300"></canvas>
            </div>
            <div style={{position: 'relative', marginTop: '0'}}>
                <img alt="chicken" src="img/배경2.png" style={{width: '100%' }} ></img>
            </div>
        </>
    )
}

const Img= styled.img`
    width: 500px;
    height: auto;
`
