import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import axios from "axios";
import { loginUser } from "../../store/User";

export default function LoginComponent() {
    const [id, setId] = useState("");
    const [password, setPassword] = useState("");
    const [userInfo, setUserInfo] = useState();
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const onIdHandler = (e) => {
        setId(e.currentTarget.value)
    }
    const onPwHandler = (e) => {
        setPassword(e.currentTarget.value)
    }
    const onSubmitHandler = (e) => {
        loginUser({id: id, pw: password})
        .then((res) => {
            if(res.data.error === false)
                {
                    console.log(res.data);
                    alert("로그인 성공");
                    setIsLoggedIn(true);
                    setUserInfo(res.data);
                    localStorage.clear()
                    localStorage.setItem("id", res.data.id)
                    localStorage.setItem("nickname", res.data.password)
                    localStorage.setItem("name", res.data.name)
                    localStorage.setItem("token", res.data.token)

                }

            else{
                alert(res.data.error_message);
            }
        })
    }

    return(
        <Positioner>
            <Logo>Login</Logo>
            <Contents>
                <form onSubmit={onSubmitHandler}>
                    <Label htmlFor="id">아이디</Label>
                    <br />
                    <Input type="text" id="id" onChange={onIdHandler} placeholder="아이디"></Input>
                    <br />
                    <Label htmlFor="password">비밀번호</Label>
                    <br />
                    <Input type="password" id="password" onChange={onPwHandler} placeholder="비밀번호"></Input>
                </form>            
            </Contents>
            <Button type="submit" onClick={onSubmitHandler}>로그인</Button>

            <RegisterLink><NavLink to="/auth/register">회원가입</NavLink></RegisterLink>

        </Positioner>
    )

}

const Positioner = styled.div`
    margin: auto;
    margin-top: 8rem;
    width: 500px;
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.8);
    position: relative;
    padding-bottom: 0.01rem;
    border-radius: 0.5rem;
`;

const Logo = styled.div`
    background-color: rgb(254,213,107);
    height: 5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    border-top-left-radius: 0.5rem;
    border-top-right-radius: 0.5rem;

    color: rgb(234,156,094);
    font-family: 'Rajdhani';
    font-size: 2.4rem;
    letter-spacing: 5px;
    text-decoration: none;
`;

const Contents = styled.div`
    background: white;
    padding: 2rem;
    height: auto;

`;

const Input = styled.input`
    width: 95%;
    border: 1px solid grey;
    border-radius: 0.5rem;
    outline: none;
    line-height: 2.5rem;
    padding-left: 0.5rem;
    padding-right: 0rem;
    margin-right: 1rem;

    font-size: 1.2rem;
    color: grey;
`;

const Label = styled.label`
    font-size: 0.8rem;
    color: grey;
`

const Button = styled.button`
    width: 92%;
    margin-top: 1rem;
    margin-left: 1rem;
    margin-right: 1rem;
    padding-top: 0.5rem;
    padding-bottom: 0.5rem;
    border-color: white;
    border-radius: 0.5rem;

    background: rgb(254,213,107);
    color: rgb(234,130,99);

    text-align: center;
    font-size: 1rem;
    font-weight: 500;

    cursor: pointer;
    user-select: none;
    transition: .2s all;
`

const RegisterLink = styled.div`
    margin-top: 0.8rem;
    margin-right: 2rem;
    margin-bottom: 1rem;

    text-align: right;
    font-size: 0.8rem;
    color: grey;
`
